import React, { Component } from 'react';
import { View,  StyleSheet, ScrollView, Modal, Text } from 'react-native';
import Events from '../Events.json';
import EventCard from './HomeComponents/EventCard';
export default class Home extends Component {
    constructor() {
        super();
        this.state = {
            name: '',
            date: [],
            location: [],
            startTime: '',
            endTime: '',
            visible: false
        }
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(clickedEvent) {
        this.setState({
            name: clickedEvent.state.name,
            date: clickedEvent.state.date,
            location: clickedEvent.state.location,
            startTime: clickedEvent.state.startTime,
            endTime: clickedEvent.state.endTime,
            visible: !clickedEvent.state.modalVisible
        });
    }

    render(){
        let events = Events.sort((event1, event2) => {
            var date1 = event1.EventDate[0] + (event1.EventDate[1] / 100);
            var date2 = event2.EventDate[0] + (event2.EventDate[1] / 100);
            return date1 - date2;
        })
        let i = 0;
        let eventsToRender = events.map(event => {
            i++;
            return (
                <EventCard
                    name={event.EventName}
                    date={event.EventDate}
                    location={event.EventLocation}
                    startTime={event.EventStartTime}
                    endTime={event.EventEndTime}
                    handleClick={this.handleClick} 
                    key={i}/>
            )
            
        })
        return (
            <View style={styles.container}>
                <ScrollView >
                    {eventsToRender}
                </ScrollView>
                <Modal visible={this.state.visible} animationType='slide' transparent={false}>
                    <Text style={styles.heading}>{this.state.name}</Text>
                </Modal>
            </View>
        )
    }

}
const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    heading: {
        alignSelf: 'center',
        paddingHorizontal: '20%',
        fontSize: 30,
        fontWeight: 'bold',
        flex: 1
    }
});
