import React, {useState} from 'react';
import { Text, View, StyleSheet, Linking } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import * as Font from 'expo-font';


export default function EventCard(props) {
    const [modalVisible, modalVisibleHandler] = useState(false);
    React.useEffect(() => {
        Font.loadAsync({
            'Roboto-Black' : require('../../assets/fonts/Roboto-Black.ttf')
        })
    }, []);
    return (
        <View style={styles.container}>
            <Text style={styles.heading}>{props.name}</Text>

            <Text style={styles.text}>Day: {props.date[0]} / {props.date[1]}</Text>

            <Text style={styles.text}>From {props.startTime} to {props.endTime}</Text>

            <TouchableOpacity style={styles.text} onPress={() => Linking.openURL(props.location[1])}>

                <Text style={{ color: 'blue' }}>
                    {props.location[0]}
                </Text>

            </TouchableOpacity>
        </View>
    )
}
const styles = StyleSheet.create({
    container: {
        alignItems: 'center',
        justifyContent: 'center',
        flex: 1,
        backgroundColor: 'red',
        borderRadius: 10,
        marginVertical: 15,
    },
    heading: {
        alignSelf: 'center',
        paddingHorizontal: '20%',
        fontSize: 30,
        fontFamily: 'Roboto-Black',
        flex: 1,
        color: 'white'
    },
    text: {
        paddingVertical: 10,
        color: 'white',
        fontSize: 20
    }
})
